package Test;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// (In this program all datatypes are covered.)
		short Z= 12348;
		switch (Z)
		{
		case 12346: System.out.println("This is for A");
		break;
		case 12347: System.out.println("This is for B");
		break;
		case 12348: System.out.println("This is for C");
		break;
		case 12345: System.out.println("This is for Z");
		break;
		default : System.out.println("This is for nun");
	
		}

		char C='W';
		switch (C)
		{
		case 'S':System.out.println("Sunday");
		break;
		case'M': System.out.println("Monday");
		break;
		case'W': System.out.println("Wednesday");
		break;
		default: System.out.println("None of above");
		
		}
		
		
		
		int day=2;
		switch (day)
		{
		case 1: System.out.println(1);
		break;
		case 2: System.out.println(2);
		break;
		case 3: System.out.println(3);
		break;
		default: System.out.println("None of above");
		}
		
		
		String city="Nashik";
		switch (city)
		{
		case "Mumbai": System.out.println("Dream City");
		break;
		case "Nagpur": System.out.println("Orange City");
		break;
		case "Nashik": System.out.println ("City of Temple");
		break;
		case "Aurangabad": System.out.println("City of gates");
		break;
		default : System.out.println("None of above");
					
		}
		byte Bell=-123;
		switch (Bell)
		{
		case -123: System.out.println("Ghanti");
		break;
		case 2: System.out.println("Banti");
		break;
		case 3: System.out.println("Santi");
		break;
		default: System.out.println("None of above");
		
		} 
		

	
		
			
		
		
	}
	

}
